import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class MyTextStyle extends TextStyle {  final Color color;  final FontWeight fontWeight;  final double size;  final String fontFamily;  const MyTextStyle({    required this.color,    this.fontWeight = FontWeight.normal,    this.size = 14,    this.fontFamily = 'Montserrat',  }) : super(          color: color,          fontWeight: fontWeight,          fontSize: size,          fontFamily: fontFamily,        );}