import 'package:flutter/material.dart';import 'Assignment10/CommonClass/CommonAppBar.dart';import 'Assignment10/CommonClass/CommonDrawer.dart';class Screen1 extends StatefulWidget {  static const String routeName = '/screen1';  const Screen1({Key? key}) : super(key: key);  @override  _Screen1State createState() => _Screen1State();}class _Screen1State extends State<Screen1> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: CommonAppBar(        title: "Wrap Demo",      ),      drawer: CommonDrawer(),      body: Container(        alignment: Alignment.topCenter,        child: Wrap(          alignment: WrapAlignment.center,          children: [            myWidget("Jaydeep"),            myWidget("Mehul Patel"),            myWidget("Riddhi"),            myWidget("Manish"),            myWidget("Khushi Mundra"),            myWidget("Namrata kacha"),            myWidget("Ruchi Kariya"),            myWidget("Dhruv Vaghela"),            myWidget("Priyank Jamvecha"),            myWidget("Anand Sharma")          ],        ),      ),    );  }  Widget myWidget(String name) {    return Container(      margin: const EdgeInsets.all(10.0),      padding: const EdgeInsets.all(10.0),      decoration: myBoxDecoration(),      child: Text(        name,        style: TextStyle(fontSize: 15.0),      ),    );  }  BoxDecoration myBoxDecoration() {    return BoxDecoration(      border: Border.all(),      borderRadius: BorderRadius.all(Radius.circular(20.0)),    );  }}