import 'package:flutter/material.dart';import 'Assignment10/CommonClass/CommonAppBar.dart';import 'Assignment10/CommonClass/CommonDrawer.dart';class Screen2 extends StatefulWidget {  const Screen2({Key? key}) : super(key: key);  @override  _Screen2State createState() => _Screen2State();}class _Screen2State extends State<Screen2> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: CommonAppBar(        title: "Stack Demo",      ),      drawer: CommonDrawer(),      body: Stack(        children: <Widget>[          myWidget(0.05, 0.03, MediaQuery.of(context).size.height / 8, MediaQuery.of(context).size.width / 1.7, Colors.blue, "Jaydeep Dhamecha"),          myWidget(0.30, 0.23, MediaQuery.of(context).size.height / 7.5, MediaQuery.of(context).size.width / 4, Colors.grey, ""),          myWidget(0.23, 0.35, MediaQuery.of(context).size.height / 7.5, MediaQuery.of(context).size.width / 4, Colors.green, ""),          myWidget(0.13, 0.50, MediaQuery.of(context).size.height / 7.5, MediaQuery.of(context).size.width / 4, Colors.red, ""),          myWidget(0.38, 0.03, MediaQuery.of(context).size.height / 7.5, MediaQuery.of(context).size.width / 3, Colors.blue, ""),          myWidget(0.48, 0.2, MediaQuery.of(context).size.height / 7.5, MediaQuery.of(context).size.width / 4, Colors.purple, ""),          myWidget(0.67, 0.63, MediaQuery.of(context).size.height / 7, MediaQuery.of(context).size.width / 3, Colors.orange, ""),          myWidget(0.58, 0.40, MediaQuery.of(context).size.height / 7, MediaQuery.of(context).size.width / 3, Colors.brown, ""),        ],      ),    );  }  Widget myWidget(double top, double right, double height, double width, Color color, String name) {    return Positioned(        top: MediaQuery.of(context).size.height * top,        right: MediaQuery.of(context).size.width * right,        child: Container(          height: height,          width: width,          decoration: myBoxDecoration(color),          child: Padding(            padding: const EdgeInsets.symmetric(vertical: 10, horizontal: 5),            child: Text(name,                textAlign: TextAlign.end,                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 22, color: Colors.white)),          ),        ));  }  BoxDecoration myBoxDecoration(Color color) {    return BoxDecoration(      border: Border.all(width: 2.0, color: Colors.black),      color: color,      boxShadow: [        BoxShadow(          color: Colors.grey,          blurRadius: 5.0,        ),      ],      borderRadius: BorderRadius.all(Radius.circular(10.0)),    );  }}