import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:fluttertoast/fluttertoast.dart';class MyHomePage extends StatefulWidget {    MyHomePage({Key? key, required this.title}) : super(key: key);  final String title;  @override  _MyHomePageState createState() => _MyHomePageState();}class _MyHomePageState extends State<MyHomePage> {  final myController = TextEditingController();  @override  void dispose() {    myController.dispose();    super.dispose();  }  void _clickButton(){    int number = int.parse(myController.text);    String displayString = "";    if (number.isEven) {      displayString = number.toString() + " is an even number";    } else {      displayString = number.toString() + " is an odd number";    }    Fluttertoast.showToast(        msg: displayString,        toastLength: Toast.LENGTH_SHORT,        gravity: ToastGravity.CENTER,        timeInSecForIosWeb: 1,        backgroundColor: Colors.red,        textColor: Colors.white,        fontSize: 18.0);  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        centerTitle: true,        title: Text(widget.title,            style: TextStyle(              color: Colors.orange,              fontSize: 20,            )),      ),      body: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: <Widget>[              Padding(                padding: EdgeInsets.symmetric(horizontal: 16, vertical: 16),                child: TextField(                  decoration: const InputDecoration(                      border: OutlineInputBorder(), labelText: 'Enter a number'),                  keyboardType: TextInputType.number,                  controller: myController,                ),              ),          ],        ),      ),      floatingActionButtonLocation: FloatingActionButtonLocation.centerFloat,      floatingActionButton: FloatingActionButton.extended(        onPressed: () {          _clickButton();        },        label: const Text('Submit'),        backgroundColor: Colors.orange,        icon: Icon(Icons.message),      ),    );  }}